const { Document, Packer, Paragraph, TextRun, ExternalHyperlink, AlignmentType, LevelFormat } = require('docx');
const fs = require('fs');

const doc = new Document({
  styles: {
    default: { document: { run: { font: "Arial", size: 22 } } }
  },
  numbering: {
    config: [
      { reference: "next-steps",
        levels: [{ level: 0, format: LevelFormat.DECIMAL, text: "%1.", alignment: AlignmentType.LEFT,
          style: { paragraph: { indent: { left: 720, hanging: 360 } } } }] },
      { reference: "resources",
        levels: [{ level: 0, format: LevelFormat.BULLET, text: "•", alignment: AlignmentType.LEFT,
          style: { paragraph: { indent: { left: 720, hanging: 360 } } } }] }
    ]
  },
  sections: [{
    properties: { page: { margin: { top: 1440, right: 1440, bottom: 1440, left: 1440 } } },
    children: [
      new Paragraph({ children: [new TextRun("Hi Ramon,")] }),
      new Paragraph({ children: [] }),
      new Paragraph({ children: [new TextRun("Great connecting with you and Courtney today. Wanted to follow up with everything we discussed.")] }),
      new Paragraph({ children: [] }),

      // What we covered
      new Paragraph({ children: [new TextRun({ text: "What we covered", bold: true })] }),
      new Paragraph({ children: [new TextRun("Your immediate priority is automating the first-pass resume screening for the ~15 open roles. The workflow would pull resumes from ADP, run them through AI filtering based on your criteria (experience level, qualifications, non-negotiables), and surface only qualified candidates to hiring managers. Courtney estimated ~300 resumes per role, which means roughly 22,500 tasks/month across all positions.")] }),
      new Paragraph({ children: [] }),
      new Paragraph({ children: [new TextRun("Beyond recruiting, you mentioned potential expansion into marketing, operations, and product development workflows once you see how this initial use case performs.")] }),
      new Paragraph({ children: [] }),

      // Pricing
      new Paragraph({ children: [new TextRun({ text: "Pricing for your volume", bold: true })] }),
      new Paragraph({ children: [new TextRun("For the recruiting use case at ~22.5K tasks/month, the "), new TextRun({ text: "Teams plan", bold: true }), new TextRun(" is the right fit:")] }),
      new Paragraph({ numbering: { reference: "resources", level: 0 }, children: [new TextRun({ text: "$249/month", bold: true }), new TextRun(" for 20K tasks, 25 users")] }),
      new Paragraph({ numbering: { reference: "resources", level: 0 }, children: [new TextRun("Overage rate: 1.25x per-task rate for anything over 20K")] }),
      new Paragraph({ numbering: { reference: "resources", level: 0 }, children: [new TextRun("Annual discount available once you've validated your volume")] }),
      new Paragraph({ children: [] }),
      new Paragraph({ children: [new TextRun("If you decide you want hands-on building support and enhanced governance controls down the line, Enterprise plans start around $65-70K/year with dedicated account management.")] }),
      new Paragraph({ children: [] }),

      // Technical note
      new Paragraph({ children: [new TextRun({ text: "Technical note: ADP connection", bold: true })] }),
      new Paragraph({ children: [new TextRun("ADP doesn't have a native AutomateFlow integration, so you'll need to connect via their API. The good news is ADP does have an API available. I'd recommend reaching out to your ADP rep to get API access set up—they've likely helped other customers with similar integrations. Once that connection is established, the rest of the workflow is straightforward.")] }),
      new Paragraph({ children: [] }),

      // Next steps
      new Paragraph({ children: [new TextRun({ text: "Next steps", bold: true })] }),
      new Paragraph({ numbering: { reference: "next-steps", level: 0 }, children: [new TextRun({ text: "Week of Feb 9:", bold: true }), new TextRun(" Start your 2-week free trial (I'll send a reminder)")] }),
      new Paragraph({ numbering: { reference: "next-steps", level: 0 }, children: [new TextRun({ text: "During trial:", bold: true }), new TextRun(" Reach out to your ADP rep about API access")] }),
      new Paragraph({ numbering: { reference: "next-steps", level: 0 }, children: [new TextRun({ text: "Feb 24:", bold: true }), new TextRun(" Follow-up call to review progress and answer questions (calendar hold sent)")] }),
      new Paragraph({ children: [] }),

      // Resources
      new Paragraph({ children: [new TextRun({ text: "Resources", bold: true })] }),
      new Paragraph({ numbering: { reference: "resources", level: 0 }, children: [
        new ExternalHyperlink({ children: [new TextRun({ text: "Security & Trust Center", style: "Hyperlink" })], link: "https://example.com/..." }),
        new TextRun(" – SOC 2, data handling, compliance details")
      ] }),
      new Paragraph({ numbering: { reference: "resources", level: 0 }, children: [
        new ExternalHyperlink({ children: [new TextRun({ text: "Terms of Service", style: "Hyperlink" })], link: "https://example.com/..." }),
        new TextRun(" – Standard agreement for your legal team to review")
      ] }),
      new Paragraph({ numbering: { reference: "resources", level: 0 }, children: [
        new ExternalHyperlink({ children: [new TextRun({ text: "Executive Ops Use Cases", style: "Hyperlink" })], link: "https://example.com/..." }),
        new TextRun(" – Examples from our EA team that might be useful for Courtney")
      ] }),
      new Paragraph({ children: [] }),

      // Closing
      new Paragraph({ children: [new TextRun("Feel free to reach out if questions come up before we connect again. Happy to help you and Courtney get this stood up.")] }),
      new Paragraph({ children: [] }),
      new Paragraph({ children: [new TextRun("Best,")] }),
      new Paragraph({ children: [new TextRun("Alex")] })
    ]
  }]
});

Packer.toBuffer(doc).then(buffer => {
  fs.writeFileSync("/Users/username/sales-workspace/deals/Artisan Brands/outputs/followup-email.docx", buffer);
  console.log("Created followup-email.docx");
});
