const { Document, Packer, Paragraph, TextRun, AlignmentType, HeadingLevel, LevelFormat } = require('docx');
const fs = require('fs');

const doc = new Document({
  styles: {
    default: { document: { run: { font: "Arial", size: 22 } } },
    paragraphStyles: [
      { id: "Heading1", name: "Heading 1", basedOn: "Normal", next: "Normal", quickFormat: true,
        run: { size: 28, bold: true, color: "000000000", font: "Arial" },
        paragraph: { spacing: { before: 300, after: 120 } } },
      { id: "Heading2", name: "Heading 2", basedOn: "Normal", next: "Normal", quickFormat: true,
        run: { size: 24, bold: true, color: "000000000", font: "Arial" },
        paragraph: { spacing: { before: 240, after: 100 } } },
    ]
  },
  numbering: {
    config: [
      { reference: "bullet-list",
        levels: [{ level: 0, format: LevelFormat.BULLET, text: "", alignment: AlignmentType.LEFT,
          style: { paragraph: { indent: { left: 720, hanging: 360 } } } }] },
      { reference: "numbered-homework",
        levels: [{ level: 0, format: LevelFormat.DECIMAL, text: "%1.", alignment: AlignmentType.LEFT,
          style: { paragraph: { indent: { left: 720, hanging: 360 } } } }] },
      { reference: "numbered-commits",
        levels: [{ level: 0, format: LevelFormat.DECIMAL, text: "%1.", alignment: AlignmentType.LEFT,
          style: { paragraph: { indent: { left: 720, hanging: 360 } } } }] },
      { reference: "numbered-next",
        levels: [{ level: 0, format: LevelFormat.DECIMAL, text: "%1.", alignment: AlignmentType.LEFT,
          style: { paragraph: { indent: { left: 720, hanging: 360 } } } }] },
    ]
  },
  sections: [{
    properties: { page: { margin: { top: 1440, right: 1440, bottom: 1440, left: 1440 } } },
    children: [
      // Header
      new Paragraph({
        children: [new TextRun({ text: "LifeGen Labs + AutomateFlow | Technical Criteria Follow-Up", bold: true, size: 32 })]
      }),
      new Paragraph({
        spacing: { after: 300 },
        children: [new TextRun({ text: "January 29, 2026", color: "000000000", size: 20 })]
      }),

      // Opening
      new Paragraph({
        spacing: { after: 200 },
        children: [new TextRun("Paul, Ethan, John ")]
      }),
      new Paragraph({
        spacing: { after: 200 },
        children: [new TextRun("Thanks for the time today with Sofia. Great conversation  I think we made solid progress narrowing down what needs to be true for this to work. Below is a recap of where we landed and the concrete next steps.")]
      }),

      // What We Covered
      new Paragraph({ heading: HeadingLevel.HEADING_1, children: [new TextRun("What We Covered")] }),

      new Paragraph({ heading: HeadingLevel.HEADING_2, children: [new TextRun("Whimsical Integration")] }),
      new Paragraph({
        numbering: { reference: "bullet-list", level: 0 },
        children: [new TextRun("Can be built as a custom API connection using their beta API")]
      }),
      new Paragraph({
        numbering: { reference: "bullet-list", level: 0 },
        children: [new TextRun("Sofia confirmed during the call that Whimsical's API does support filtering by team section or folder  which addresses the permissions concern")]
      }),
      new Paragraph({
        numbering: { reference: "bullet-list", level: 0 },
        spacing: { after: 200 },
        children: [new TextRun({ text: "Key dependency:", bold: true }), new TextRun(" Ethan to confirm API access and filtering capabilities with Whimsical team")]
      }),

      new Paragraph({ heading: HeadingLevel.HEADING_2, children: [new TextRun("SharePoint as Knowledge Source")] }),
      new Paragraph({
        numbering: { reference: "bullet-list", level: 0 },
        children: [new TextRun("We can pull files without storing them on AutomateFlow's side")]
      }),
      new Paragraph({
        numbering: { reference: "bullet-list", level: 0 },
        children: [new TextRun("Folder-level permission controls are available")]
      }),
      new Paragraph({
        numbering: { reference: "bullet-list", level: 0 },
        children: [new TextRun({ text: "Critical requirement you flagged:", bold: true }), new TextRun(" You want AI summarization from the content  not just \"here's a link to a file.\" Understood and agreed. Sofia James demo exactly this.")]
      }),

      new Paragraph({ heading: HeadingLevel.HEADING_2, children: [new TextRun("Monday.com Read/Write")] }),
      new Paragraph({
        numbering: { reference: "bullet-list", level: 0 },
        spacing: { after: 200 },
        children: [new TextRun("Straightforward  already supported on your current Teams plan. You can start testing this now.")]
      }),

      new Paragraph({ heading: HeadingLevel.HEADING_2, children: [new TextRun("Permission Controls")] }),
      new Paragraph({
        numbering: { reference: "bullet-list", level: 0 },
        children: [new TextRun("Sofia demoed the admin center: restricted/allowed apps by team, read-only vs. read-write permissions per user group")]
      }),
      new Paragraph({
        numbering: { reference: "bullet-list", level: 0 },
        spacing: { after: 200 },
        children: [new TextRun("This addresses the Finance drive concern  you can lock down which apps and actions each team can use")]
      }),

      new Paragraph({ heading: HeadingLevel.HEADING_2, children: [new TextRun("Usage Logging")] }),
      new Paragraph({
        numbering: { reference: "bullet-list", level: 0 },
        children: [new TextRun("No built-in conversation analytics dashboard")]
      }),
      new Paragraph({
        numbering: { reference: "bullet-list", level: 0 },
        spacing: { after: 200 },
        children: [new TextRun({ text: "Solution:", bold: true }), new TextRun(" Build custom logging to AutomateFlow Tables + scheduled AI reports for usage patterns and duplicate question detection. Data retention is configurable (7-30 days).")]
      }),

      // Your Homework
      new Paragraph({ heading: HeadingLevel.HEADING_1, children: [new TextRun("Your Homework")] }),
      new Paragraph({
        numbering: { reference: "numbered-homework", level: 0 },
        children: [
          new TextRun({ text: "Ethan: Whimsical API access", bold: true }),
          new TextRun("  Reach out to your Whimsical contact to confirm: (a) you can get an API token (still in beta), and (b) their API supports filtering by team/folder so you can scope what each agent can access.")
        ]
      }),
      new Paragraph({
        numbering: { reference: "numbered-homework", level: 0 },
        children: [
          new TextRun({ text: "Team: Test Monday.com on your current account", bold: true }),
          new TextRun("  You have a Teams 50K plan with significant headroom. Credentials are under user@user (I've attached the account details below). Play around with the Monday.com integration  it's one of the easier ones to validate quickly.")
        ]
      }),
      new Paragraph({
        numbering: { reference: "numbered-homework", level: 0 },
        spacing: { after: 200 },
        children: [
          new TextRun({ text: "Paul: Start outlining the business case", bold: true }),
          new TextRun("  I'll provide pricing context below. To help build the case for CFO/CEO, I've set up a value calculator we can use together: "),
          new TextRun({ text: "AutomateFlow-roi-calculator.vercel.app/c/hc0l2e", bold: true }),
          new TextRun(". It's a starting point  we can refine the numbers together once you have a better sense of task volumes and use cases.")
        ]
      }),

      // Our Commits
      new Paragraph({ heading: HeadingLevel.HEADING_1, children: [new TextRun("What We've Committed To")] }),
      new Paragraph({
        numbering: { reference: "numbered-commits", level: 0 },
        children: [
          new TextRun({ text: "Sofia: SharePoint demo video (async)", bold: true }),
          new TextRun("  During our company summit next week, Sofia James put together a video showing: SharePoint  AI analysis  summarized response (not just file links). We'll send this before our Feb 9th call.")
        ]
      }),
      new Paragraph({
        numbering: { reference: "numbered-commits", level: 0 },
        children: [
          new TextRun({ text: "Alex: Account access details", bold: true }),
          new TextRun("  Done. See my earlier email with the Sequr account info attached.")
        ]
      }),
      new Paragraph({
        numbering: { reference: "numbered-commits", level: 0 },
        spacing: { after: 200 },
        children: [
          new TextRun({ text: "Alex: Pricing context", bold: true }),
          new TextRun("  See below.")
        ]
      }),

      // Pricing
      new Paragraph({ heading: HeadingLevel.HEADING_1, children: [new TextRun("On Pricing")] }),
      new Paragraph({
        spacing: { after: 200 },
        children: [new TextRun("I want to be transparent here: I can't give you a hard number until we've nailed down the use cases. Pricing depends on expected task volume, number of users, and the specific integrations you need  and that variability matters.")]
      }),
      new Paragraph({
        spacing: { after: 200 },
        children: [new TextRun("That said, to help with your planning: "), new TextRun({ text: "Enterprise customers typically land in the $50K$100K/year range.", bold: true }), new TextRun(" That includes the platform, the task volume you'd need for these AI workflows, and  critically  hands-on support from a TAM who James actually build with you.")]
      }),
      new Paragraph({
        spacing: { after: 200 },
        children: [new TextRun("I'm happy to walk through how pricing works on our next call so you can model scenarios against your expected usage. In the meantime, the value calculator linked above James let you play with different assumptions and see how ROI scales with task volume. But the honest answer is: we need to define what success looks like before I can quote you accurately.")]
      }),

      // Next Steps
      new Paragraph({ heading: HeadingLevel.HEADING_1, children: [new TextRun("Next Steps")] }),
      new Paragraph({
        numbering: { reference: "numbered-next", level: 0 },
        children: [
          new TextRun({ text: "Week of Feb 9th:", bold: true }),
          new TextRun(" I'll send a calendar hold for a follow-up call. We'll review Sofia's SharePoint demo, discuss what you learned from Whimsical, and work through the business case together.")
        ]
      }),
      new Paragraph({
        numbering: { reference: "numbered-next", level: 0 },
        children: [
          new TextRun({ text: "Before then:", bold: true }),
          new TextRun(" If you have questions as you're testing Monday.com or digging into Whimsical, just reply here  happy to help.")
        ]
      }),

      // Closing
      new Paragraph({ spacing: { before: 300 }, children: [] }),
      new Paragraph({
        children: [new TextRun("Looking forward to keeping the momentum going.")]
      }),
      new Paragraph({ spacing: { before: 200 }, children: [new TextRun("Alex")] }),
    ]
  }]
});

Packer.toBuffer(doc).then(buffer => {
  fs.writeFileSync("/Users/username/sales-workspace/deals/LifeGen Labs/outputs/LifeGen Labs-technical-followup-000000000.docx", buffer);
  console.log("Created LifeGen Labs-technical-followup-000000000.docx");
});
