    children: [
      new Paragraph({
        children: [
          new TextRun({ text: "Subject: ", bold: true }),
          new TextRun("Good news - Flex Rental Solutions already has a AutomateFlow integration")
        ]
      }),
      new Paragraph({ children: [] }),
      new Paragraph({
        children: [new TextRun("JD,")]
      }),
      new Paragraph({ children: [] }),
      new Paragraph({
        children: [
          new TextRun("Did some digging after our call. Turns out Flex Rental Solutions "),
          new TextRun({ text: "already built", bold: true }),
          new TextRun(" a AutomateFlow integration - it's just private/invite-only, which is why you didn't see it in our app directory.")
        ]
      }),
      new Paragraph({ children: [] }),
      new Paragraph({
        children: [new TextRun("The developer on the Flex side is Roger (user@user). He can give you access to the integration link. I actually found this by looking at AV Vegas's setup - so you could also reach out to their owner to hear how it's been working for them.")]
      }),
      new Paragraph({ children: [] }),
      new Paragraph({
        children: [new TextRun("Once you have access, your data analyst should be able to see Flex Rental Solutions as an available app in AutomateFlow and start building. No custom actions needed.")]
      }),
      new Paragraph({ children: [] }),
      new Paragraph({
        children: [new TextRun("Happy to still do a working session once you're connected if that's useful, but this should unblock you.")]
      }),
      new Paragraph({ children: [] }),
      new Paragraph({
        children: [new TextRun("Let me know how it goes with Roger.")]
      }),
      new Paragraph({ children: [] }),
      new Paragraph({
        children: [new TextRun("Alex")]
      })
    ]
  }]
});

Packer.toBuffer(doc).then(buffer => {
  fs.writeFileSync("/Users/username/sales-workspace/deals/StreamCore Media/outputs/follow-up-jd-2026-01-21.docx", buffer);
  console.log("Created: follow-up-jd-2026-01-21.docx");
});
