const { Document, Packer, Paragraph, TextRun, AlignmentType } = require('docx');
const fs = require('fs');

const doc = new Document({
  styles: {
    default: { document: { run: { font: "Arial", size: 22 } } }
  },
  sections: [{
    properties: { page: { margin: { top: 1440, right: 1440, bottom: 1440, left: 1440 } } },
    children: [
      new Paragraph({ children: [new TextRun("James,")] }),
      new Paragraph({ children: [] }),
      new Paragraph({ children: [new TextRun("That's a fair framing, and I appreciate you being direct about the org's priorities. Let me push back gently on the practical vs. management dichotomy—because the governance features ARE what protect the operational metrics you care about.")] }),
      new Paragraph({ children: [] }),
      new Paragraph({ children: [new TextRun("Think about the use cases we mapped out together:")] }),
      new Paragraph({ children: [new TextRun("• SalesCloud ↔ TaskBoard sync taking context switches from 15-20/day down to 2-3")] }),
      new Paragraph({ children: [new TextRun("• CPL detection going from 1-3 days (manual review) to <1 hour (automated)")] }),
      new Paragraph({ children: [new TextRun("• Onboarding capacity scaling from 5/week to 15-20/week")] }),
      new Paragraph({ children: [] }),
      new Paragraph({ children: [new TextRun("Those improvements only deliver value if the automations "), new TextRun({ text: "keep running correctly", italics: true }), new TextRun(". Without governance—role-based access, audit trails, visibility into who built what—you're one rogue edit away from another Black Friday situation. Except this time at 10x the volume with your 2,000-customer target.")] }),
      new Paragraph({ children: [] }),
      new Paragraph({ children: [new TextRun("The math: we estimated ~$124K in annual value from these use cases. At a $55K investment, the governance premium isn't overhead—it's insurance on the 2.3x return. The alternative is building on self-serve and hoping nothing breaks when you can least afford it.")] }),
      new Paragraph({ children: [] }),
      new Paragraph({ children: [new TextRun({ text: "Offer:", bold: true }), new TextRun(" Happy to join a call with whoever owns budget priorities on your side. Sometimes it helps to have the vendor in the room to reframe what the investment actually buys. Let me know if that would be useful.")] }),
      new Paragraph({ children: [] }),
      new Paragraph({ children: [new TextRun("In the meantime, keep testing both platforms—that's exactly the right move.")] }),
      new Paragraph({ children: [] }),
      new Paragraph({ children: [new TextRun("Alex")] })
    ]
  }]
});

Packer.toBuffer(doc).then(buffer => fs.writeFileSync("/Users/username/sales-workspace/deals/Velocity Systems/outputs/James-governance-response.docx", buffer));
