const tableBorder = { style: BorderStyle.SINGLE, size: 1, color: "CCCCCC" };
const cellBorders = { top: tableBorder, bottom: tableBorder, left: tableBorder, right: tableBorder };

const createRow = (label, value, isHeader = false) => {
  const labelCell = new TableCell({
    borders: cellBorders,
    width: { size: 3000, type: WidthType.DXA },
    shading: { fill: isHeader ? "FF4A00" : "F5F5F5", type: ShadingType.CLEAR },
    children: [new Paragraph({ children: [new TextRun({ text: label, bold: true, color: isHeader ? "FFFFFF" : "000000000", size: 22 })] })]
  });
  const valueCell = new TableCell({
    borders: cellBorders,
    width: { size: 6360, type: WidthType.DXA },
    shading: { fill: isHeader ? "FF4A00" : "FFFFFF", type: ShadingType.CLEAR },
    children: [new Paragraph({ children: [new TextRun({ text: value, color: isHeader ? "FFFFFF" : "000000000", size: 22 })] })]
  });
  return new TableRow({ children: [labelCell, valueCell] });
};

const doc = new Document({
  styles: {
    default: { document: { run: { font: "Arial", size: 22 } } }
  },
  sections: [{
    properties: { page: { margin: { top: 1440, right: 1440, bottom: 1440, left: 1440 } } },
