const { Document, Packer, Paragraph, TextRun, ExternalHyperlink, AlignmentType, LevelFormat } = require('docx');
const fs = require('fs');

const doc = new Document({
  styles: {
    default: { document: { run: { font: "Arial", size: 22 } } }
  },
  numbering: {
    config: [
      {
        reference: "next-steps",
        levels: [{
          level: 0,
          format: LevelFormat.BULLET,
          text: "•",
          alignment: AlignmentType.LEFT,
          style: { paragraph: { indent: { left: 720, hanging: 360 } } }
        }]
      },
      {
        reference: "eval-criteria",
        levels: [{
          level: 0,
          format: LevelFormat.DECIMAL,
          text: "%1.",
          alignment: AlignmentType.LEFT,
          style: { paragraph: { indent: { left: 720, hanging: 360 } } }
        }]
      }
    ]
  },
  sections: [{
    properties: {
      page: { margin: { top: 1440, right: 1440, bottom: 1440, left: 1440 } }
    },
    children: [
      // Greeting
      new Paragraph({
        spacing: { after: 200 },
        children: [new TextRun("Sarah,")]
      }),

      // Thanks
      new Paragraph({
        spacing: { after: 200 },
        children: [new TextRun("Thanks for the great conversation today with you and David. Exciting to see the ProcureSoft connection working and the invoice workflow coming together.")]
      }),

      // Eval criteria header
      new Paragraph({
        spacing: { before: 200, after: 100 },
        children: [new TextRun({ text: "Evaluation Criteria We Aligned On", bold: true })]
      }),

      // Criteria list
      new Paragraph({
        numbering: { reference: "eval-criteria", level: 0 },
        children: [new TextRun({ text: "Citizen development usability", bold: true }), new TextRun(" - primary differentiator vs IntegrateHub")]
      }),
      new Paragraph({
        numbering: { reference: "eval-criteria", level: 0 },
        children: [new TextRun({ text: "Central IT build capability", bold: true }), new TextRun(" - ease of building complex workflows")]
      }),
      new Paragraph({
        numbering: { reference: "eval-criteria", level: 0 },
        children: [new TextRun({ text: "Pricing", bold: true }), new TextRun(" - already validated in range")]
      }),
      new Paragraph({
        numbering: { reference: "eval-criteria", level: 0 },
        spacing: { after: 200 },
        children: [new TextRun({ text: "InfoSec controls", bold: true }), new TextRun(" - table stakes, Emma to review")]
      }),

      // Deck link
      new Paragraph({
        spacing: { after: 200 },
        children: [
          new TextRun("Full details in the deck: "),
          new ExternalHyperlink({
            children: [new TextRun({ text: "Pilot Evaluation Slides", color: "0066CC", underline: {} })],
            link: "https://example.com/..."
          })
        ]
      }),

      // Next steps header
      new Paragraph({
        spacing: { before: 200, after: 100 },
        children: [new TextRun({ text: "Next Steps", bold: true })]
      }),

      // Next steps list
      new Paragraph({
        numbering: { reference: "next-steps", level: 0 },
        children: [new TextRun({ text: "Tomorrow: ", bold: true }), new TextRun("Your 1:1 with Robert to discuss pilot approval ($20K for 2 months)")]
      }),
      new Paragraph({
        numbering: { reference: "next-steps", level: 0 },
        children: [new TextRun({ text: "Tomorrow: ", bold: true }), new TextRun("Jordan demos the full invoice workflow to Lisa")]
      }),
      new Paragraph({
        numbering: { reference: "next-steps", level: 0 },
        children: [new TextRun({ text: "Emma: ", bold: true }), new TextRun("Review security/legal details (I'll follow up separately)")]
      }),
      new Paragraph({
        numbering: { reference: "next-steps", level: 0 },
        children: [new TextRun({ text: "Once Robert gives verbal approval: ", bold: true }), new TextRun("We send formal quote")]
      }),
      new Paragraph({
        numbering: { reference: "next-steps", level: 0 },
        spacing: { after: 200 },
        children: [new TextRun({ text: "Target: ", bold: true }), new TextRun("Pilot contract signed next week")]
      }),

      // Workshop confirmation
      new Paragraph({
        spacing: { after: 200 },
        children: [new TextRun("Confirmed on the calendar: "), new TextRun({ text: "Feb 18 IT Leadership Workshop", bold: true }), new TextRun(" (9:30am ET). Rachel Mabie is ready to run the \"Build Your Own Agent\" session.")]
      }),

      // BrandFolder excitement
      new Paragraph({
        spacing: { after: 200 },
        children: [new TextRun("Also excited about the BrandFolder facial recognition use case you mentioned - that's a great second project for the pilot period once invoice processing is stable.")]
      }),

      // Closing
      new Paragraph({
        spacing: { after: 200 },
        children: [new TextRun("Let me know if there's anything I can clarify before your conversation with Robert tomorrow.")]
      }),

      // Sign-off
      new Paragraph({
        spacing: { before: 200 },
        children: [new TextRun("Best,")]
      }),
      new Paragraph({
        children: [new TextRun("Alex")]
      })
    ]
  }]
});

Packer.toBuffer(doc).then(buffer => {
  fs.writeFileSync('/Users/username/sales-workspace/deals/Horizon Ventures/outputs/jan29-followup-email.docx', buffer);
  console.log('Created: jan29-followup-email.docx');
});
