const { Document, Packer, Paragraph, TextRun, AlignmentType, LevelFormat } = require('docx');
const fs = require('fs');

const doc = new Document({
  styles: {
    default: { document: { run: { font: "Arial", size: 22 } } }
  },
  numbering: {
    config: [
      { reference: "bullets", levels: [{ level: 0, format: LevelFormat.BULLET, text: "•", alignment: AlignmentType.LEFT, style: { paragraph: { indent: { left: 720, hanging: 360 } } } }] },
      { reference: "next-steps", levels: [{ level: 0, format: LevelFormat.DECIMAL, text: "%1.", alignment: AlignmentType.LEFT, style: { paragraph: { indent: { left: 720, hanging: 360 } } } }] }
    ]
  },
  sections: [{
    properties: { page: { margin: { top: 1440, right: 1440, bottom: 1440, left: 1440 } } },
    children: [
      new Paragraph({ spacing: { after: 200 }, children: [new TextRun({ text: "Subject: ", bold: true }), new TextRun("Great connecting today - next steps on AutomateFlow + Agents")] }),
      new Paragraph({ spacing: { after: 200 }, children: [] }),
      new Paragraph({ spacing: { after: 200 }, children: [new TextRun("Hakan,")] }),
      new Paragraph({ spacing: { after: 200 }, children: [new TextRun("Thanks for making time today, and for walking us through what you've built. The legal case management system you've created is genuinely impressive - the complexity of those workflows (68 steps with branching logic) is something we typically only see from dedicated automation engineers.")] }),

      new Paragraph({ spacing: { before: 300, after: 200 }, children: [new TextRun({ text: "What we covered:", bold: true })] }),
      new Paragraph({ numbering: { reference: "bullets", level: 0 }, spacing: { after: 100 }, children: [new TextRun("Brian confirmed the security architecture for Agents: data transits the same way as standard Zaps, LLM processing runs through Anthropic (with AWS Bedrock), and we have explicit agreements with subprocessors that no customer data is used for training")] }),
      new Paragraph({ numbering: { reference: "bullets", level: 0 }, spacing: { after: 100 }, children: [new TextRun({ text: "One follow-up on data usage: ", bold: true }), new TextRun("You can also opt out of AutomateFlow using your data for AI model training on your current plan - just fill out the opt-out form at AutomateFlow.com/security-compliance. Enterprise plans auto-opt out all users, which may be relevant to the Engin conversation.")] }),
      new Paragraph({ numbering: { reference: "bullets", level: 0 }, spacing: { after: 100 }, children: [new TextRun("Your plan to move Excel macro logic into Agents makes sense for stability - the current spikes in your usage are likely tied to macro retry behavior")] }),
      new Paragraph({ numbering: { reference: "bullets", level: 0 }, spacing: { after: 200 }, children: [new TextRun("You're upgrading to the 50K plan to accommodate current growth")] }),

      new Paragraph({ spacing: { before: 300, after: 200 }, children: [new TextRun({ text: "Next steps:", bold: true })] }),
      new Paragraph({ numbering: { reference: "next-steps", level: 0 }, spacing: { after: 100 }, children: [new TextRun({ text: "Brian ", bold: true }), new TextRun("James send over the architecture documentation via TeamChat")] }),
      new Paragraph({ numbering: { reference: "next-steps", level: 0 }, spacing: { after: 100 }, children: [new TextRun({ text: "You ", bold: true }), new TextRun("James connect with Engin to discuss whether an enterprise partnership makes sense for NetPro Solutions")] }),
      new Paragraph({ numbering: { reference: "next-steps", level: 0 }, spacing: { after: 200 }, children: [new TextRun({ text: "We ", bold: true }), new TextRun("can schedule a follow-up with you, Engin, and whoever else should be involved once you've had that conversation")] }),

      new Paragraph({ spacing: { before: 300, after: 200 }, children: [new TextRun({ text: "On enterprise partnership:", bold: true })] }),
      new Paragraph({ spacing: { after: 200 }, children: [new TextRun("Two things that might be relevant as you talk to Engin:")] }),
      new Paragraph({ spacing: { after: 200 }, children: [new TextRun({ text: "For you specifically", bold: true }), new TextRun(" - enterprise plans include ~30,000 agent actions built in (vs. the 100 on Teams with add-ons). If you're serious about using Agents for document analysis, that headroom matters. We'd also help bring your colleagues up to speed so you're not the only one who knows how to build and maintain these workflows.")] }),
      new Paragraph({ spacing: { after: 200 }, children: [new TextRun({ text: "For Engin and the AI transformation initiative", bold: true }), new TextRun(" - we work with companies globally on making AI an operational lever, not just a productivity tool for drafting emails. The difference between \"everyone has ChatGPT access\" and \"AI is embedded in how we actually run the business\" is significant, and it's the work Engin seems to be driving. Happy to share what we're seeing work.")] }),

      new Paragraph({ spacing: { before: 200, after: 200 }, children: [new TextRun("Let me know how the conversation with Engin goes - I'll hold time flexible for a follow-up.")] }),
      new Paragraph({ spacing: { before: 200 }, children: [new TextRun("Alex")] })
    ]
  }]
});

Packer.toBuffer(doc).then(buffer => {
  fs.writeFileSync("/Users/username/sales-workspace/deals/NetPro Solutions/outputs/hakan-followup-2026-01-21.docx", buffer);
  console.log("Created: hakan-followup-2026-01-21.docx");
});
