async function createPresentation() {
  const pptx = new pptxgen();
  pptx.layout = 'LAYOUT_16x9';
  pptx.author = 'Alex Obleton';
  pptx.title = 'PathMark Analytics Account Review';

  // Write HTML files
  for (const [name, html] of Object.entries(slides)) {
    fs.writeFileSync(path.join(outputDir, `${name}.html`), html);
  }

  // Slide 1: Title
  await html2pptx(path.join(outputDir, 'slide1.html'), pptx);

  // Slide 2: Usage chart
  const { slide: slide2, placeholders: ph2 } = await html2pptx(path.join(outputDir, 'slide2.html'), pptx);

  slide2.addChart(pptx.charts.BAR, [{
    name: 'Billable Tasks (K)',
    labels: ['Oct', 'Nov', 'Dec', 'Jan'],
    values: [73, 107, 129, 145]
  }], {
    ...ph2[0],
    barDir: 'col',
    showTitle: false,
    showLegend: false,
    showCatAxisTitle: false,
    showValAxisTitle: false,
    valAxisMinVal: 0,
    valAxisMaxVal: 160,
    valAxisMajorUnit: 40,
    chartColors: ['FF4A00'],
    dataLabelPosition: 'outEnd',
    showValue: true,
    dataLabelFontSize: 10,
    dataLabelColor: '2C2C2C'
  });

  // Add 100K limit line
  slide2.addShape(pptx.shapes.LINE, {
    x: ph2[0].x + 0.2,
    y: ph2[0].y + ph2[0].h * (1 - 100/160),
    w: ph2[0].w - 0.4,
    h: 0,
    line: { color: '2C2C2C', width: 1.5, dashType: 'dash' }
  });
  slide2.addText('100K limit', {
    x: ph2[0].x + ph2[0].w - 0.9,
    y: ph2[0].y + ph2[0].h * (1 - 100/160) - 0.2,
    w: 0.9,
    h: 0.2,
    fontSize: 8,
    color: '000000000'
  });

  // Slide 3: Top Zaps chart
  const { slide: slide3, placeholders: ph3 } = await html2pptx(path.join(outputDir, 'slide3.html'), pptx);

  slide3.addChart(pptx.charts.BAR, [{
    name: 'Tasks (K)',
    labels: ['Work Order Zap', 'Scheduled Tasks', 'Contribution Tracking', 'Everything Else'],
    values: [154, 37, 27, 112]
  }], {
    ...ph3[0],
    barDir: 'bar',
    showTitle: false,
    showLegend: false,
    showCatAxisTitle: false,
    showValAxisTitle: false,
    chartColors: ['FF4A00', 'FF8C66', 'FFB899', 'CCCCCC'],
    barGapWidthPct: 40,
    dataLabelPosition: 'outEnd',
    showValue: true,
    dataLabelFontSize: 10,
    dataLabelColor: '2C2C2C'
  });

  // Slide 4: Optimization
  await html2pptx(path.join(outputDir, 'slide4.html'), pptx);

  // Slide 5: Recommendation
  await html2pptx(path.join(outputDir, 'slide5.html'), pptx);

  // Slide 6: Real Cost + Enterprise
  await html2pptx(path.join(outputDir, 'slide6.html'), pptx);

  // Slide 7: Next Steps
  await html2pptx(path.join(outputDir, 'slide7.html'), pptx);

  // Save
  await pptx.writeFile({ fileName: path.join(outputDir, 'PathMark Analytics-account-review.pptx') });
  console.log('Presentation created: PathMark Analytics-account-review.pptx');

  // Cleanup HTML files
  for (const name of Object.keys(slides)) {
    fs.unlinkSync(path.join(outputDir, `${name}.html`));
  }
}

createPresentation().catch(console.error);
