async function createPresentation() {
  const pptx = new pptxgen();
  pptx.layout = 'LAYOUT_16x9';
  pptx.author = 'AutomateFlow';
  pptx.title = 'Cool Rooms - Finance Discovery';

  // Write and process each slide
  for (let i = 0; i < slides.length; i++) {
    const htmlPath = path.join(TMPDIR, `slide${i + 1}.html`);
    fs.writeFileSync(htmlPath, slides[i]);
    await html2pptx(htmlPath, pptx, { tmpDir: TMPDIR });
    console.log(`Processed slide ${i + 1}`);
  }

  // Save presentation
  const outputPath = '/Users/username/sales-workspace/deals/ChillSpace Tech/outputs/CoolRooms-Finance-Discovery.pptx';
  await pptx.writeFile({ fileName: outputPath });
  console.log(`Presentation saved to: ${outputPath}`);
}

